// noinspection JSAnnotator
return (function (e) {
  if (e.value) {
    return true
  }

  if (!window.PublicKeyCredential) {
    alert('This browser does not support WebAuthn!');
    return false;
  }

  function bufferDecode(value) {
    return Uint8Array.from(atob(value), c => c.charCodeAt(0));
  }

  function bufferEncode(value) {
    return btoa(String.fromCharCode.apply(null, new Uint8Array(value)))
      .replace(/\+/g, '-')
      .replace(/\//g, '_')
      .replace(/=/g, '');
  }

  const opt = {"publicKey":{"challenge":"fcmbWlWF2daBQLS4rkzrW+0/ivUFYVBIOqb+ilsI/cA=","rp":{"name":"Ory Corp","id":"localhost"},"user":{"name":"placeholder","icon":"https://via.placeholder.com/128","displayName":"placeholder","id":"9UqfgBimRbasL1iOEeAKYw=="},"pubKeyCredParams":[{"type":"public-key","alg":-7},{"type":"public-key","alg":-35},{"type":"public-key","alg":-36},{"type":"public-key","alg":-257},{"type":"public-key","alg":-258},{"type":"public-key","alg":-259},{"type":"public-key","alg":-37},{"type":"public-key","alg":-38},{"type":"public-key","alg":-39},{"type":"public-key","alg":-8}],"authenticatorSelection":{"requireResidentKey":false,"userVerification":"preferred"},"timeout":60000}}
  opt.publicKey.user.id = bufferDecode(opt.publicKey.user.id);
  opt.publicKey.challenge = bufferDecode(opt.publicKey.challenge);

  if (opt.publicKey.excludeCredentials) {
    opt.publicKey.excludeCredentials = opt.publicKey.excludeCredentials.map(function (value) {
      return {
        ...value,
        id: bufferDecode(value.id)
      }
    })
  }

  navigator.credentials.create(opt).then(function (credential) {
    e.value = JSON.stringify({
      id: credential.id,
      rawId: bufferEncode(credential.rawId),
      type: credential.type,
      response: {
        attestationObject: bufferEncode(credential.response.attestationObject),
        clientDataJSON: bufferEncode(credential.response.clientDataJSON),
      },
    })
    console.log("setting", e.value)
    e.click()
  }).catch((err) => {
    alert(err)
  })

  return false
})(this)
